import React, { Component } from 'react';
import './App.css';

class Card extends Component {

// 0 = false 1 = true (1 = false 2 = true)
handleClick = (event) => {
    if (event.target.name === event.target.value) {
        this.props.updateScore() 
    }
    console.log(event.target.name)
    console.log(event.target.innerText)
}    


    render() {
        // console.log(this.props.q)
        return(
            <div>
            {this.props.q.map(question => {
                return (
                    <div key={question.text} className="card">
                <div>{question.text}</div>
                    {question.answer}
                <button name={question.answer} onClick={event => this.handleClick(event)}>True</button>
                <button name={question.answer} onClick={event => this.handleClick(event)}>False</button>
                </div>
            )})}
            </div>
        )
    }
}

export default Card;
