import React, { Component } from "react";
import questions from "./data";
import Submit from "./Submit";
import Score from "./Score";
import Card from "./Card";
import NewGame from "./NewGame";

export default class App extends Component {
  state = {
    score: 0,
    name: "",
    players: {
      name: "",
      score: ""
    }
  };

  submitScore = score => {
    this.setState({ score: score });
  };

  updateScore = () => {
    let prevState = this.state.score
    this.setState( {score: prevState + 1})
    console.log("updated")
  }

  updateName = (name) => {
    this.setState({name: name},
    () => document.querySelector(".main").innerHTML = `<div> ${this.state.name} finished with ${this.state.score}</div>`)
    // Congratulations {this.state.name} Your total score is {this.state.score}
    this.setState({players: {...this.state.players, name}})
  }




  render() {
    console.log(this.state);
    return (
      <div className="main">
        <Score score={this.state.score} />
        <NewGame/>
        <Card q={questions.questions} updateScore={this.updateScore}/>
        <Submit submitScore={this.submitScore} updateName={this.updateName}/>
      </div>
    );
  } 
}
