}

    render() {
        return (
            <div>
                <form ref={form => (this.form =  form)} onSubmit={this.handleSubmit}>
                    <input type="text" placeholder="Name"/>
                    <input type="submit" value="Submit " />

                </form>

                <form>
  <label>
    Name:
    <input type="text" name="name" />
  </label>
  <input type="submit" value="Submit" />
</form>
            </div>
        );
    }
}

export default Submit;
