import React, { Component } from 'react';
import './App.css';

class Card extends Component {


handleClick = (event) => {
    if (this.props.q.answer === event.target.value) {
        this.props.updateScore() 
    }
}    


    render() {
        // console.log(this.props.q)
        return(
            <div>
            {this.props.q.map(question => {
                return (
                <div key={question.text} className="card">
                <div>{question.text}</div>
                <button onClick={event => this.handleClick}>True</button>
                <button onClick={event => this.handleClick(event)}>False</button>
                </div>
            )})}
            </div>
        )
    }
}

export default Card;
